settings.custom_liquid:
{% if product.metafields.custom.full_specification_2 != blank %}
<div class="full-specification ">
<div class="commonSecHead anim fadeInUp">
                    <h2>Full Specification </h2>
                </div>
  <div class="rich-text  color-{{ section.settings.color_scheme }} gradient{% if section.settings.full_width %} rich-text--full-width content-container--full-width{% endif %}">
      {{ product.metafields.custom.full_specification_2 }}
  </div>
</div>
{% endif %}

{% if product.metafields.custom.more_about_product_big_section != blank %}
<div class="common-section moreAbout-section bg-white">
                <div class="commonSecHead anim fadeInUp">
                    <h2>More about product</h2>
                </div>
                <div class="commonSeContent">
                    <ul class="moreAboutProduct row">
                      {% if product.metafields.custom.more_about_product_big_section != blank %}
                        <li class="fl-column fl-col-xl-6">
                            <div class="productCard videoCard anim fadeInUp">
                                {{ product.metafields.custom.more_about_product_big_section }}
                            </div>
                        </li>
                      {% endif %}
                      {% if product.metafields.custom.more_about_product_small_section_1 != blank %}
                        <li class="fl-column fl-col-xl-3">
                            <div class="productCard anim fadeInUp">
                                {{ product.metafields.custom.more_about_product_small_section_1 }}
                            </div>
                        </li>
                      {% endif %}
                      {% if product.metafields.custom.more_about_product_small_section_2 != blank %}
                        <li class="fl-column fl-col-xl-3">
                            <div class="productCard anim fadeInUp">
                               {{ product.metafields.custom.more_about_product_small_section_2 }}
                            </div>
                        </li>
                        {% endif %}
                      {% if product.metafields.custom.more_about_product_small_section_3 != blank %}
                        <li class="fl-column fl-col-xl-2">
                            <div class="productCard anim fadeInUp">
                                {{ product.metafields.custom.more_about_product_small_section_3 }}
                            </div>
                        </li>
                        {% endif %}
                      {% if product.metafields.custom.more_about_product_small_section_3 != blank %}
                        <li class="fl-column fl-col-xl-4">
                            <div class="productCard anim fadeInUp">
                                {{ product.metafields.custom.more_about_product_small_section_4 }}
                            </div>
                        </li>
                        {% endif %}
                        {% if product.metafields.custom.more_about_product_small_section_5 != blank %}
                          <li class="fl-column fl-col-xl-3">
                              <div class="productCard anim fadeInUp">
                                  {{ product.metafields.custom.more_about_product_small_section_5 }}
                              </div>
                          </li>
                        {% endif %}
                      {% if product.metafields.custom.more_about_product_small_section_6 != blank %}
                        <li class="fl-column fl-col-xl-3">
                            <div class="productCard anim fadeInUp">
                                {{ product.metafields.custom.more_about_product_small_section_6 }}
                            </div>
                        </li>
                     {% endif %}   
                    </ul>
                </div>

        </div>
{% endif %}
